export async function reportComments(homeworkTopic, commonMistakes){
    const endpoint_ai = "https://api.openai.com/v1/chat/completions";
    const options = {
        method: 'POST',
        headers: {
            'Content-Type': 'application/json',
            'Authorization': 'Bearer sk-proj-bNVXL7Z8l9STzRJDiSaYT3BlbkFJ6iMMwVowurzK51hMPpsq'
        },
        body: JSON.stringify({
            model: 'gpt-4',
            temperature: 0.1,
            messages: [
                {"role": "system", 
                "content": "You are a Computer Science Teacher who needs to give general feedback of a homework to a group. As well as giving them some recommendations or areas of improvement. Write a little paragraph, around 6 lines, describing to a teacher what were the most common mistakes for a homework where some comments were noted. Considering what the topic of the homework was, write some recommendations for the teacher to apply with the group. It should be 2 or 3 lines long. Give me the answers in a JSON format {feedback: your feedback, recommendations: your recommendations}. Please only return the JSON with such structure."},
                {"role": "user",
                "content": `Topic of the homework: ${homeworkTopic}
                comments made: ${commonMistakes}`
            }]
        })
    };

    try {
        const response = await fetch(endpoint_ai, options);
        if (!response.ok) {
            throw new Error('Error al generar los comentarios');
        }
        const data = await response.json();
        // Extraer la respuesta generada por la API
        const respuesta = JSON.parse(data.choices[0].message.content);
        const feedback = respuesta.feedback;
        const recommendations = respuesta.recommendations;
    
        console.log(feedback);
        console.log(recommendations);
    
        // Devolver la respuesta en un objeto JSON
        return { feedback, recommendations };
    } catch (error) {
        console.error(error);
        return { error: 'Error al generar los comentarios' };
    }
    
}

export function generatePDF(group, subject, mainText, recommendedActions, studentsWithDifficulties, regularAndOutstandingStudents) {
    // Crear una nueva instancia de jsPDF
    const doc = new jsPDF();

    const imgWidth = 100; // Ancho de la imagen
    const imgHeight = 30; // Altura de la imagen
    const xImg = (doc.internal.pageSize.width - imgWidth) / 2; // Posición X de la imagen

    // Agregar la imagen en el centro
    doc.addImage(imgData, 'JPEG', xImg, 10, imgWidth, imgHeight);

    // Establecer la fuente y el tamaño para el subtítulo
    doc.setFont("times", "bold");
    doc.setFontSize(15);

    const subtitle = `Reporte del grupo ${group} materia: ${subject}`;
    const subtitleWidth = doc.getStringUnitWidth(subtitle) * doc.internal.getFontSize() / doc.internal.scaleFactor;
    const xSubtitle = (doc.internal.pageSize.width - subtitleWidth) / 2;
    doc.text(subtitle, xSubtitle, 60);

    const mainTextLines = doc.splitTextToSize(mainText, 230); 

    // Establecer la fuente y el tamaño para el texto principal (párrafo)
    doc.setFont("times", "normal");
    doc.setFontSize(12);
    doc.text(mainTextLines, 10, 80);

    // Agregar el campo "Acciones Recomendadas"
    doc.setFont("times", "bold");
    doc.text("Acciones Recomendadas:", 10, 120);
    doc.setFont("times", "normal");
    
    const recommendedActionsLines = doc.splitTextToSize(recommendedActions, 170); 
    doc.text(recommendedActionsLines, 10, 130);

    // Agregar los alumnos con dificultades
    doc.setFont("times", "bold");
    doc.text("Alumnos con dificultades", 20, 160);
    doc.setFont("times", "normal");
    let y = 170;
    studentsWithDifficulties.forEach(student => {
        doc.text(student, 20, y);
        y += 10;
    });

    // Agregar los alumnos regulares y sobresalientes
    doc.setFont("times", "bold");
    doc.text("Alumnos regulares y sobresalientes", 110, 160);
    doc.setFont("times", "normal");
    y = 170;
    regularAndOutstandingStudents.forEach(student => {
        doc.text(student, 110, y);
        y += 10;
    });

    // Guardar el PDF
    doc.save('reporte20.pdf');
}

export async function generatePdfFromComments(group, subject, commonMistakes, studentsWithDifficulties, regularAndOutstandingStudents) {
    try {
        // Llamar a reportComments para obtener el feedback y las recomendaciones
        const { feedback, recommendations } = await reportComments(subject, commonMistakes);

        // Verificar si ocurrió algún error en reportComments
        if (feedback === undefined || recommendations === undefined) {
            throw new Error('Error al obtener los comentarios');
        }

        // Llamar a generatePdf con los datos obtenidos y los demás parámetros
        generatePDF(group, subject, feedback, recommendations, studentsWithDifficulties, regularAndOutstandingStudents);
    } catch (error) {
        console.error(error);
    }
}